<template>  <MainLayout>    <div class="space-y-6">      <!-- Header -->      <div class="flex justify-between items-center">        <h1 class="text-2xl font-bold">User Details</h1>        <button          @click="goBack"          class="px-4 py-2 bg-gray-700 rounded text-white"        >          Back        </button>      </div>      <!-- Profile -->      <div class="bg-[#1C1F2E] p-6 rounded-xl shadow-md">        <h2 class="text-xl font-semibold mb-3">Profile</h2>        <div class="grid grid-cols-2 gap-4 text-gray-300">          <p><b>Name:</b> {{ user.first_name }} {{ user.last_name }}</p>          <p><b>Email:</b> {{ user.email }}</p>          <p><b>Role:</b> {{ user.role }}</p>          <p><b>Status:</b> {{ user.status }}</p>        </div>      </div>	  	  <!-- ROLE UPDATE -->	<div class="mt-4">	  <label class="block text-gray-400 mb-1">Change Role</label>	  	  <select v-model="role" class="bg-gray-800 border px-3 py-2 rounded w-64">		<option value="user">User</option>		<option value="admin">Admin</option>		<option value="accounts">Accounts</option>		<option value="compliance">Compliance</option>	  </select>	  <button		@click="updateRole"		class="ml-3 px-4 py-2 bg-blue-600 text-white rounded"	  >		Save	  </button>	</div>      <!-- KYC -->      <div class="bg-[#1C1F2E] p-6 rounded-xl">        <h2 class="text-xl font-semibold mb-3">KYC</h2>        <div v-if="kyc">          <p><b>Status:</b> {{ kyc.status }}</p>          <p><b>ID Type:</b> {{ kyc.id_type }}</p>          <p><b>ID Number:</b> {{ kyc.id_number }}</p>        </div>        <div v-else class="text-gray-400">No KYC submitted.</div>      </div>      <!-- Wallets -->      <div class="bg-[#1C1F2E] p-6 rounded-xl">        <h2 class="text-xl font-semibold mb-3">Wallets</h2>        <div v-for="w in wallets" :key="w.id">          <p><b>{{ w.currency }}:</b> â‚¦{{ w.balance }}</p>        </div>      </div>      <!-- Transactions -->      <div class="bg-[#1C1F2E] p-6 rounded-xl">        <h2 class="text-xl font-semibold mb-3">Transactions</h2>        <table class="w-full text-left text-gray-300">          <thead>            <tr>              <th>Date</th>              <th>Type</th>              <th>Amount</th>            </tr>          </thead>          <tbody>            <tr v-for="t in transactions" :key="t.id">              <td>{{ t.created_at }}</td>              <td>{{ t.type }}</td>              <td>{{ t.amount }}</td>            </tr>          </tbody>        </table>      </div>    </div>  </MainLayout></template><script setup>import { ref, onMounted } from "vue";import axios from "axios";import MainLayout from "@/Layouts/MainLayout.vue";import { useRoute, useRouter } from "vue-router";const route = useRoute();const router = useRouter();const user = ref({});const kyc = ref(null);const wallets = ref([]);const transactions = ref([]);const role = ref("");const load = async () => {  const res = await axios.get(`/api/admin/users/${route.params.id}`);  const d = res.data.data;  user.value = d.user;  kyc.value = d.kyc;  wallets.value = d.wallets;  transactions.value = d.transactions;};onMounted(() => {  load();  role.value = user.value.role;});const updateRole = async () => {  await axios.post(`/api/admin/users/${route.params.id}/role`, {    role: role.value  });  alert("Role updated");};const goBack = () => router.push("/admin/users");onMounted(load);</script>